//3cc2f8f2
import React from 'react';
import { useEffect } from 'react';
import './App.css';
import searchIcon from './search.svg';

const API_URL = 'http://www.omdbapi.com/?i=tt3896197&apikey=3cc2f8f2'

const movie1 = {
    
    "Title": "UFC 18: Road to the Heavyweight Title",
    "Year": "1999",
    "imdbID": "tt0483513",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BZjI2N2UyMDctOTYwZi00ZjYwLWFjOTItZjIwN2Q5ZWYwYzY0XkEyXkFqcGdeQXVyMDE4MzA0NQ@@._V1_SX300.jpg"
    
}

const App = () => {

    const searchMov = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();

        console.log(data.Search);
    }
    useEffect (() => {
        searchMov('Batman');
    }, []);
    return (
        <div className = "app">
            <h1>Movie Lab</h1>

            <div className="search">
                <input
                    placeholder="search for movies"
                    value="Superman"
                    onChange={() => {}}
                />

                <img src={searchIcon}
                alt="search"
                onClick={() => {}}
                />
            </div>
            <div className="container">
                <div className="movie">
                    <div>
                        <p>{movie1.Year}</p>
                    </div>
                    <div>
                        <img src={movie1.Poster} alt={movie1.Title}/>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
