//3cc2f8f2
import React from 'react';
import { useEffect, useState } from 'react';
import './App.css';
import searchIcon from './search.svg';
import movieCard from './moviecard';

const API_URL = 'http://www.omdbapi.com/?i=tt3896197&apikey=3cc2f8f2'

const movie1 = {
    
    "Title": "UFC 18: Road to the Heavyweight Title",
    "Year": "1999",
    "imdbID": "tt0483513",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BZjI2N2UyMDctOTYwZi00ZjYwLWFjOTItZjIwN2Q5ZWYwYzY0XkEyXkFqcGdeQXVyMDE4MzA0NQ@@._V1_SX300.jpg"
    
}

const App = () => {

    const [movies, setMovies] = useState([]);
    const searchMov = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();

        setMovies(data.Search);
    }
    useEffect (() => {
        searchMov('Batman');
    }, []);
    return (
        <div className = "app">
            <h1>Movie Lab</h1>

            <div className="search">
                <input
                    placeholder="search for movies"
                    value="Superman"
                    onChange={() => {}}
                />

                <img src={searchIcon}
                alt="search"
                onClick={() => {}}
                />
            </div>
            {
                movies?.length > 0
                ? (
                    <div className="container">
                        <movieCard movie1={movies[0]}/>
                    </div>
                ) : (
                    <div className="empty">
                        <h2>No Movies Found</h2>
                    </div>
                )
            }
        </div>
    );
}


export default App;
